import React from 'react'
import styled from '@emotion/styled'
import Tap from '../molecules/Tap'


interface Props {
    categories?:['커피','디저트,']
    selected: number
    onClickCategory(event: React.MouseEvent<HTMLButtonElement>): void
  }




const Header = () => {
  return (
    <>
      <HeaderWrapper>
        <HeaderTitle> 키오스크</HeaderTitle>
      </HeaderWrapper>
      <TabWrapper>
      {categories.length ? (
          categories.map(({ id, name }) => (
            <Tap
              key={id}
              id={id}
              category={name}
              active={selected === id}
              onClickCategory={onClickCategory}
            />
          ))
        ) : (
          <h1>데이터가 없습니다.</h1>
        )}
      </TabWrapper>

    </>
  )
}


const HeaderWrapper = styled.div`
  margin: 0 auto;
  text-align: center;
  margin-top: 60px;
  margin-bottom: 60px;
`
const HeaderTitle = styled.div`
  font-size: 50px;
  font-weight: 800;
`

const TabWrapper = styled.div`
  margin-top: 30px;
  display: flex;
  justify-items: center;
  justify-content: space-around;
`

export default Header
