import React from 'react';
import useCartStore from '../store/cart.store';

function Cart() {
  const { cartList } = useCartStore();

  // 수량을 증가시키는 함수
  const increaseQuantity = (index) => {
    const updatedCartList = [...cartList];
    updatedCartList[index].productAmount++;
    // 업데이트된 카트 목록을 전역 상태에 적용
    useCartStore.setState({ cartList: updatedCartList });
  };

  // 수량을 감소시키는 함수
  const decreaseQuantity = (index) => {
    const updatedCartList = [...cartList];
    if (updatedCartList[index].productAmount > 1) {
      updatedCartList[index].productAmount--;
      // 업데이트된 카트 목록을 전역 상태에 적용
      useCartStore.setState({ cartList: updatedCartList });
    }
  };

  return (
    <div>
      <h2>장바구니</h2>
      {cartList.map((item, index) => (
        <div key={index}>
          <div>상품 정보: {item.productName}, 온도: {item.productTemperature}, 사이즈: {item.productSize}</div>
          <div>수량: 
            <button onClick={() => decreaseQuantity(index)}>-</button>
            {item.productAmount}
            <button onClick={() => increaseQuantity(index)}>+</button>
          </div>
        </div>
      ))}
      <div>
        총가격: {/* 여기에 총가격을 계산하여 출력하는 코드를 추가하면 됩니다 */}
      </div>
      <div>
        <div>전체 취소</div>
        <div>결제</div>
      </div>
    </div>
  );
}

export default Cart;
