import { create } from "zustand";


interface Orderstore{

    productName:string;
    productSize:string;
    productTemp:string;

    setProduct:()=>void;
}

const useOrderStore=create<Orderstore>(set=>({


    productName: "",
    productSize: "",
    productTemp: "",
    setProduct:()=>set(state=>({...state,productSize:"",productTemp:""}))


}));

export default useOrderStore;
