import React, { useState } from 'react';
import useCartStore from '../store/cart.store';
import './style.css';
import Payment from '../Pay';

function Cart() {
  const { cartList } = useCartStore();
  const[payOpen,setPayOpen]=useState(false)

  //~~~~~~~~~
  const handleConfirmOrder = () => {
    const postData = jsonData; // 이전에 생성한 주문 정보 데이터

    // POST 요청을 보내는 코드 여기에 추가
    fetch('/api/v1/Kiosk/order-product', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(postData),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log('Order successfully placed:', data);
      // 성공적으로 주문을 처리한 후에 필요한 작업을 여기에 추가
    })
    .catch(error => {
      console.error('Error placing order:', error);
      // 주문을 처리하는 동안 에러가 발생한 경우 처리할 내용을 여기에 추가
    });
  };

  const jsonData = cartList.map(item => ({
    productId:item.productId,
    productName:item.productName,
    orderProductSize:item.productSize,
    orderProductTemperature:item.productTemperature,
    orderProductAmount:item.productAmount,
    orderProductPrice:item.newProductPrice
  }))

  // 수량을 증가시키는 함수
  const increaseQuantity = (index:number) => {
    if (index >= 0 && index < cartList.length) {
      const updatedCartList = [...cartList];
      updatedCartList[index].productAmount++;
      updatedCartList[index].newProductPrice = updatedCartList[index].productPrice * updatedCartList[index].productAmount;
      // 업데이트된 카트 목록을 전역 상태에 적용
      useCartStore.setState({ cartList: updatedCartList });
    } else {
      console.error("Invalid index:", index);
    }
  };

  // 수량을 감소시키는 함수
  const decreaseQuantity = (index:number) => {
    if (index >= 0 && index < cartList.length) {
      const updatedCartList = [...cartList];
      if (updatedCartList[index].productAmount > 1) {
        updatedCartList[index].productAmount--;
        updatedCartList[index].newProductPrice = updatedCartList[index].productPrice * updatedCartList[index].productAmount;
        // 업데이트된 카트 목록을 전역 상태에 적용
        useCartStore.setState({ cartList: updatedCartList });
      }
    } else {
      console.error("Invalid index:", index);
    }
  };

  // 상품을  삭제하는 함수
  const removeProduct = (index:number) => {
    if (index >= 0 && index < cartList.length) {
      const updatedCartList = cartList.filter((_, i) => i !== index);
      // 업데이트된 카트 목록을 전역 상태에 적용
      useCartStore.setState({ cartList: updatedCartList });
    } else {
      console.error("Invalid index:", index);
    }
  };
const payOpenHandler=()=>{
  setPayOpen(true);
}
// 총 수량 계산 함수
const calculateTotalQuantity = () => {
  let totalQuantity = 0;
  cartList.forEach(item => {
    totalQuantity += item.productAmount;
  });
  return totalQuantity;
};
  // 총가격 계산 함수
const calculateTotalPrice = () => {
  let totalPrice = 0;
  cartList.forEach(item => {

    totalPrice += item.newProductPrice;
  });
  return totalPrice;
};

  const cancelAllProducts = () => {
    // 카트 목록을 빈 배열로 업데이트하여 전체 상품 취소
    useCartStore.setState({ cartList: [] });
  };
// 담긴 상품이 없으면 장바구니 랜더링 x
  if(!cartList.length) {
       return(
        <></>

       );}

 const closePaymentModal = () => {
        setPayOpen(false);
  };

  console.log(cartList);
  return (

    <>

    <div className='cart'>
      {cartList.map((item, index) => (
       <div className='orderItems' key={index}>
          <img src={item.productImage} />
          {item.productName}
          {item.productTemperature &&item.productTemperature &&(
          <div>옵션:{item.productTemperature}사이즈:{item.productSize}</div>
          )}       
        <div>
            <button onClick={() => decreaseQuantity(index)}>-</button>
            {item.productAmount}
            <button onClick={() => increaseQuantity(index)}>+</button>
            <button onClick={() => removeProduct(index)}>X</button> {/* 삭제 버튼 */}
            <div>{item.newProductPrice}</div>
        </div>
        </div>
      ))}
      <div className='detail' >
       <div> 총수량: {calculateTotalQuantity()}</div>
       <div>총가격: {calculateTotalPrice()}</div>
      </div>
            <button onClick={handleConfirmOrder}>>확인</button>
      <div className='buttons'>
      <div className='orderButton' onClick={payOpenHandler}>결제</div>
      <div className='allCancelButton' onClick={cancelAllProducts}>전체 취소</div>
      {payOpen&&(
      <Payment 
      handleClosePaymentModal={closePaymentModal}
      cartlist={cartList}
      totalPrice={calculateTotalPrice()} 




      />
      )}
      </div>
    </div>

    </>
  );
}

export default Cart;
