import React, { useEffect, useState } from 'react';
import './style.css';
import useOrderStore from '../../store/order.store';


interface Product {
  productId: number;
  productName: string;
  productImage: string;
  productPrice: number;
  productHasHot: boolean;
  productHasIce: boolean;
  productHasLarge: boolean;
  productHasMedium: boolean;
  productHasSmall: boolean;

}

interface MenuOptionProps {
  product: Product | null;
}



interface OptionProps extends MenuOptionProps  {
  handleTempButtonClick: (temp: string) => void;
}



function MenuOption({ product }: MenuOptionProps) {

const[selectedTemp,setSelectedTemp]=useState("")
const[selectedSize,setSelectedSize]=useState("");
const[amout,setAmount]=useState(1);

const {productTemp,setProductTemp } = useOrderStore();

useEffect(() => {
  setProductTemp(selectedTemp)
  console.log("store",productTemp);
}, [selectedTemp,setProductTemp]); // selectedTemp가 변경될 때마다 useEffect 실행

  if (!product) {
    return null; // 선택된 상품이 없을 경우 아무것도 렌더링하지 않음
  }
  const handleTempButtonClick = (temp: string) => {
    setSelectedTemp(temp);

  };
  const handleSizeButtonClick = (size: string) => {
    setSelectedSize(size);

  };
  return (

    <div className='wrap'>
      <div className='contents'>
        {product.productName}
        {product.productPrice}
        <div className='optionWrap'>
        <Option product={product} handleTempButtonClick={handleTempButtonClick}/>

       </div>
      </div>
      <button className='addButton'>담기</button>
    </div>

  )
}



function Option({product,handleTempButtonClick }:OptionProps){

  return(
    <>
    {product?.productHasHot&&
     <button onClick={() => handleTempButtonClick("hot")}>HOT</button>
    }
    {product?.productHasSmall&&
     <button onClick={() => handleTempButtonClick("ice")}>ICE</button>
    }
    {product?.productHasLarge&&
     <button onClick={() => handleTempButtonClick("large")}>LARGE</button>
    }
    {product?.productHasMedium&&
     <button onClick={() => handleTempButtonClick("Large")}>MEDIUM</button>
    }
    {product?.productHasSmall&&
     <button onClick={() => handleTempButtonClick("ice")}>SMALL</button>
    }

    </>
  );
}



export default MenuOption
