import styles from './style.module.css'
import MenuOption from './MenuOption';
import { useState } from 'react';

interface Product {
  productId: number;
  productName: string;
  productImage: string;
  productPrice: number;
  productHasHot: boolean;
  productHasIce: boolean;
  productHasLarge: boolean;
  productHasMedium: boolean;
  productHasSmall: boolean;
}

interface Category {
  categoryId: number;
  categoryName: string;
  categoryDatetime: string;
  products: Product[];
}

interface ProductsProps {
  categoryList: Category[];
}

export default function MenuItem({ categoryList }: ProductsProps){

//state: 메뉴 옵션 보기 상태
const [selectedProduct, setSelectedProduct] = useState<number>(1);

  const handleProductClick = (productId:number) => {
    const selectedProduct = products.find((product) => product.productId === productId);
    setSelectedProduct(selectedProduct);
  };

//event handler: 메뉴 옵션 상자보기 클릭 이벤트 처리 

    return(
        <>
        <div className={styles.menuContainer} >

        { products.map((item,key)=>(
        <div className={styles.menuItem}key={key} onClick={() => handleProductClick(item.productId)}>
          <img src={item.productImage} alt={item.productName} />
      <span>{item.productName}</span>
      <span>{`${item.productPrice}원`}</span>
      </div>
       ))}
       <MenuOption selectedProduct={selectedProduct} />
       </div>

        </>
    );
}
