import React, { useEffect, useState } from 'react';
import styles from './style.module.css';
import CategoryButton from './CategoryButton';
import useSpeechToText from '../Speech';

interface Product {
  id: number;
  name: string;
  categoryId: number;
}

interface CategoryNavbarProps {
  categories: { categoryId: number; categoryName: string }[];
  products: Product[];
  handleCategoryClick: (categoryId: number) => void;
  activeCategoryId: number;
}

const CategoryNavbar: React.FC<CategoryNavbarProps> = ({
  categories,
  products,
  handleCategoryClick,
  activeCategoryId,
}) => {
  const { transcript, listening, toggleListening } = useSpeechToText();
  const [activeCategoryName, setActiveCategoryName] = useState<string>('');

  useEffect(() => {
    // Set active category name based on transcript
    categories.forEach(category => {
      if (transcript.toLowerCase().includes(category.categoryName.toLowerCase())) {
        setActiveCategoryName(category.categoryName);
        handleCategoryClick(category.categoryId); // Automatically select the category
      }
    });
  }, [transcript, categories, handleCategoryClick]);

  useEffect(() => {
    // When the active category changes, scroll to the top of the product list
    const productList = document.getElementById('productList');
    if (productList) {
      productList.scrollTop = 0;
    }
  }, [activeCategoryId]);

  return (
    <>
      <div className={styles.CategoryWrapper}>
        <div className={styles.CategoryTitle}>개발자 키오스크</div>
        <textarea className="transcript" value={transcript} onChange={() => {}} />
        <button onClick={toggleListening}>{listening ? '음성인식 중지' : '음성인식 시작'}</button>
      </div>
      <div className={styles.categoryNavbar}>
        {categories.map(category => (
          <CategoryButton
            key={category.categoryId}
            categoryId={category.categoryId}
            categoryName={category.categoryName}
            isActive={category.categoryName === activeCategoryName}
            onClick={() => handleCategoryClick(category.categoryId)}
          />
        ))}
      </div>
      <div id="productList" className={styles.productList}>
        {/* Display products based on active category */}
        {products
          .filter(product => product.categoryId === activeCategoryId)
          .map(product => (
            <div key={product.id}>{product.name}</div>
          ))}
      </div>
    </>
  );
};

export default CategoryNavbar;
