import CategoryList from '../Types/Interface/Category-List.Interface';
import styles from './style.module.css'



interface Props{
  categoryId: number;
  categoryName: string;
  isActive: boolean;
  handleCategoryClick: (categoryId: number) => 
   void;
}




const CategoryButton=({categoryId,categoryName,isActive,handleCategoryClick}:Props) =>{


  const className=styles.categoryButton;
  return (


      <button  onClick={()=>handleCategoryClick(categoryId)} className={isActive ? `${className} ${styles.active}` : className}>{categoryName}</button> 

  )
}

export default CategoryButton
