import CategoryList from '../Types/Interface/Category-List.Interface';
import './style.css';

interface Props{
  categoryList:CategoryList
  isActive: boolean;
  handleCategoryClick: (categoryId: number) => void;
}


//top3
const CategoryButton=({categoryList,isActive,handleCategoryClick}:Props) =>{
  const {categoryName,categoryId}=categoryList

  const className=style.CategoryButton;
  return (


      <button className='categoryButton' onClick={()=>handleCategoryClick(categoryId)}>{categoryName}</button> 

  )
}

export default CategoryButton
