import { create } from "zustand";
import Cart from "../Types/Interface/CartList.Interface";


interface cartstore{

    cartList:Cart[];




}

const useOrderStore=create<cartstore>(set=>({

    productId:"",
    productName: "",
    productSize: "",
    productTemp: "",
    setProductName:(productName)=>set(state=>({...state,productName})),
    setProductSize:(productSize)=>set(state=>({...state,productSize})),
    setProductTemp:(productTemp)=>set(state=>({...state,productTemp})),


}));

export default useOrderStore;
