import React, { useEffect, useState } from 'react';
import styles from './style.module.css';
import MenuItem from '../MenuItem';
import CategoryNavbar from '../Navbar/CategoryNavbar';
import { CategoryList } from '../Types/Interface';
import { GetCategoryResponseDto } from '../Apis/Response/Category';
import { getCategoryRequest } from '../Apis';
import { ResponseDto } from '../Apis/Response';




export default function Home() {
  const [selectedCategoryId, setSelectedCategoryId] = useState<number>(0);
  const [mainAnimationClassName, setMainAnimationClassName] = useState<string>(styles.fadeEnter);
  const handleCategoryClick = (clickCategoryId: number) => {
    if (clickCategoryId === selectedCategoryId) return;
    setMainAnimationClassName(styles.fadeLeave);

    setTimeout(() => {
      setMainAnimationClassName(styles.fadeEnter);
      setSelectedCategoryId(clickCategoryId);
    }, 500);

  };

  const [categoryData, setCategoryData] = useState<CategoryList[]>([])

  const getCategoryResponse =(responseBody:GetCategoryResponseDto |ResponseDto|null)=>{

    if(!responseBody) return;
    const{code}=responseBody;
    if(code ==='DBE') alert('데이터베이스 오류 입니다.');
    if(code !=='SU') return;

    const {categoryList}=responseBody as GetCategoryResponseDto;
    setCategoryData(categoryList);

  }


useEffect(()=>{
getCategoryRequest().then(getCategoryResponse)

},[])

  return (
    <div className={styles.home}>
       <CategoryNavbar
        handleCategoryClick= {handleCategoryClick}
        selectedCategoryId={selectedCategoryId} 
        categories={categoryData}/>
       <MenuItem/>
    </div>
  );
}
