import React from 'react'
import './style.css';


interface Product {
  productId: number;
  productName: string;
  productImage: string;
  productPrice: number;
  productHasHot: boolean;
  productHasIce: boolean;
  productHasLarge: boolean;
  productHasMedium: boolean;
  productHasSmall: boolean;
}






function MenuOption(
{
  productId,
  productName,
  productImage,
  productPrice, 
  productHasHot, 
  productHasIce,
  productHasLarge,
  productHasMedium,
  productHasSmall,
}:Product) {


  return (
    <div className='wrap'>
      <div className='contents'>
        <div className='productInfo'>
          <img src={productImage} alt={productName} />
          <div>
            <h3>{productName}</h3>
            <p>{productPrice}원</p>
            <p>{productHasHot ? 'Hot 가능' : 'Hot 불가능'}</p>
            <p>{productHasIce ? 'Ice 가능' : 'Ice 불가능'}</p>
            {/* 필요한 상품 정보를 추가로 표시할 수 있습니다 */}
          </div>
        </div>
        <div className='optionWrap'>
          <TempOption />
          <SizeOption />
          <AmountCounter />
        </div>
      </div>
      <button className='addButton'>담기</button>
    </div>
  );

}


function TempOption(){
  return(
    <>

    a


    </>
  );
}


function SizeOption(){
  return(
    <>

    b


    </>
  );
}


function AmountCounter(){


  return(
    <>

    <div className='counter'></div>

    <button>-</button>
    <span></span>
    <button>+</button>
    </>
  );
}






export default MenuOption
