import React from 'react'
import styled from '@emotion/styled'
import Tap from '../molecules/Tap'
const categories = [
    { id: 1, name: "카테고리 1" },
    { id: 2, name: "카테고리 2" },
    { id: 3, name: "카테고리 3" },
    { id: 4, name: "카테고리 4" },
    { id: 5, name: "카테고리 5" }
  ];

interface Props {
    categories?:[]
    selected: number
    onClickCategory(event: React.MouseEvent<HTMLButtonElement>): void
  }




const Header = ({ categories, onClickCategory, selected }) => {
    if (!categories) {
        return (
          <TabWrapper>

          </TabWrapper>
        )
      }

  return (
    <>
      <HeaderWrapper>
        <HeaderTitle> 키오스크</HeaderTitle>
      </HeaderWrapper>
      <TabWrapper>
      {categories.length ? (
          categories.map(({ id, name }) => (
            <Tap
              key={id}
              id={id}
              category={name}
              active={selected === id}
              onClickCategory={onClickCategory}
            />
          ))
        ) : (
          <h1>데이터가 없습니다.</h1>
        )}
      </TabWrapper>

    </>
  )
}


const HeaderWrapper = styled.div`
  margin: 0 auto;
  text-align: center;
  margin-top: 60px;
  margin-bottom: 60px;
`
const HeaderTitle = styled.div`
  font-size: 50px;
  font-weight: 800;
`

const TabWrapper = styled.div`
  margin-top: 30px;
  display: flex;
  justify-items: center;
  justify-content: space-around;
`

export default Header
