import { useState } from 'react';
import AddButton from './AddButton';

import styles from './OrderModal.module.css';
import OrderProduct from '../Types/Interface/Order-Product.Interface';
import { ProductList } from '../Types/Interface';
import MenuItem from '../MenuItem';
import MenuOption from './MenuOption/MenuOption';
import Button from '../atoms/Button';

interface OrderModalProps {
  menu: ProductList;
  closeOrderModal: () => void;
  handleAddOrder: (menuOrder: OrderProduct) => void;
}

export default function OrderModal({ menu, closeOrderModal, handleAddOrder }: OrderModalProps) {
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedTemp, setSelectedTemp] = useState('');
  const [amount, setAmount] = useState(1);
  const sendOrderData = (handleAddOrder: (menuOrder: OrderProduct) => void) => {
    const orderData = {
      productId:menu.productId,
      name:menu.productName,
      size:selectedSize,
      temperature:selectedTemp,
      amount:amount
    };
    handleAddOrder(orderData);
  };
  const isLargeSize = selectedSize === 'Large';

  return (
    <div className={styles.wrap}>
      <Button label={'X'} className={styles.closeButton} onClick={closeOrderModal}></Button>
      <div className={styles.contents}>


        <MenuOption
          hasLarge={menu.productHasLarge}
          hasSmall={menu.productHasSmall}
          hasHot={menu.productHasHot}
          hasIce={menu.productHasIce}
          selectedSize={selectedSize}
          selectedTemp={selectedTemp}
          setSelectedSize={setSelectedSize}
          setSelectedTemp={setSelectedTemp}
          amount={amount}
          setAmount={setAmount}
        />
      </div>
      <AddButton
        handleAddOrder={handleAddOrder}
        closeOrderModal={closeOrderModal}
        sendOrderData={sendOrderData}
        selectedSize={selectedSize}
        selectedTemp={selectedTemp}
      />
    </div>
  );
}
