import React, { useState } from 'react';
import styles from './style.module.css';
import MenuOption from './MenuOption';

interface ProductProps {
  products: {
    productName: string;
    productImage: string;
    productPrice: number;
    productHasHot: boolean;
    productHasIce: boolean;
    productHasLarge: boolean;
    productHasMedium: boolean;
    productHasSmall: boolean;
  }[];
}

export default function MenuItem({ products }: ProductProps) {
  const [selectedProduct, setSelectedProduct] = useState<any>(null);

  // 메뉴 옵션 보기 클릭 이벤트 처리
  const handleMenuOptionClick = (product: any) => {
    setSelectedProduct(product);
  };

  return (
    <div className={styles.menuContainer}>
      {/* MenuOption을 클릭했을 때만 해당 상품 정보 전달 */}
      {selectedProduct && <MenuOption product={selectedProduct} />}
      {products.map((item, key) => (
        <div
          className={styles.menuItem}
          key={key}
          onClick={() => handleMenuOptionClick(item)} // 각 상품 클릭 시 handleMenuOptionClick 호출하여 선택된 상품 정보 전달
        >
          <img src={item.productImage} alt={item.productName} />
          <span>{item.productName}</span>
          <span>{`${item.productPrice}원`}</span>
        </div>
      ))}
    </div>
  );
}
