import React, { useEffect, useState } from 'react';
import styles from './style.module.css';
import CategoryButton from './CategoryButton';
import useSpeechToText from '../Speech';

interface CategoryNavbarProps {
  categories: { categoryId: number; categoryName: string }[];
  handleCategoryClick: (categoryId: number) => void;
  activeCategoryId: number;
}

export default function CategoryNavbar({ categories, handleCategoryClick, activeCategoryId }: CategoryNavbarProps) {
  const { transcript } = useSpeechToText();
  const [activatedCategories, setActivatedCategories] = useState<number[]>([]); // 활성화된 카테고리 목록을 관리

  useEffect(() => {
    // 음성 인식 결과가 변경될 때마다 카테고리를 검사하여 일치하는 카테고리를 찾음
    const matchedCategory = categories.find(category => category.categoryName === transcript);
    if (matchedCategory) {
      const categoryId = matchedCategory.categoryId;
      // 활성화된 카테고리 목록에 추가
      setActivatedCategories(prevState => [...prevState, categoryId]);
      // 일치하는 카테고리를 클릭하는 함수 호출
      handleCategoryClick(categoryId);
    }
  }, [categories, transcript, handleCategoryClick]);

  return (
    <>
      <div className={styles.CategoryWrapper}>
        <div className={styles.CategoryTitle}>개발자 키오스크</div>
      </div>
      <div className={styles.categoryNavbar}>
        {categories.map(category => (
          <CategoryButton
            key={category.categoryId}
            categoryId={category.categoryId}
            categoryName={category.categoryName}
            isActive={activatedCategories.includes(category.categoryId) || category.categoryId === activeCategoryId} // 활성화된 상태일 때 또는 현재 활성화된 카테고리일 때
            onclick={() => handleCategoryClick(category.categoryId)}
          />
        ))}
      </div>
    </>
  );
}
