// MenuOption.js

import React, { useEffect, useState } from 'react';
import './style.css';
import useCartStore from '../../store/cart.store';

interface Product {
  productId: number;
  productName: string;
  productImage: string;
  productPrice: number;
  productHasHot: boolean;
  productHasIce: boolean;
  productHasLarge: boolean;
  productHasMedium: boolean;
  productHasSmall: boolean;
}

interface MenuOptionProps {
  product: Product;
  onClose: () => void;
}

interface OptionProps {
  product: Product;
  handleTempButtonClick: (temp: string) => void;
  handleSizeButtonClick: (size: string) => void;
}

function MenuOption({ product, onClose }: MenuOptionProps) {
  const [selectedTemp, setSelectedTemp] = useState("");
  const [selectedSize, setSelectedSize] = useState("");
  const [amount, setAmount] = useState(1);
  const [productPrice, setProductPrice] = useState(0);
  const { addToCart } = useCartStore();

  useEffect(() => {
    setProductPrice(product.productPrice * amount);
  }, [amount, product]);

  const handleTempButtonClick = (temp: string) => {
    setSelectedTemp(temp);
  };

  const handleSizeButtonClick = (size: string) => {
    setSelectedSize(size);
  };

  const increaseAmount = () => {
    if (amount < 10) setAmount(amount + 1);
  };

  const decreaseAmount = () => {
    if (amount > 1) setAmount(amount - 1);
  };

  const handleAddToCart = () => {
    addToCart({
      productId: product.productId,
      productName: product.productName,
      productImage: product.productImage,
      productSize: selectedSize,
      productTemperature: selectedTemp,
      productAmount: amount,
      newProductPrice: productPrice,
      productPrice: product.productPrice
    });
    onClose();
  };

  return (
    <div className='wrap'>
      <button className='Xbutton' onClick={onClose}>X</button>
      <div className='contents'>
        <div className='imgBox'>
          <img src={product.productImage} alt={product.productName} />
          <div>{product.productPrice}원</div>
        </div>
        <Option product={product} handleTempButtonClick={handleTempButtonClick} handleSizeButtonClick={handleSizeButtonClick} selectedTemp={selectedTemp} selectedSize={selectedSize} />
      </div>
      <div className='counter'>
        <button onClick={decreaseAmount}>-</button>
        <span>{amount}</span>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div> 가격:{productPrice}</div>
      <button className='addButton' onClick={handleAddToCart}>담기</button>
    </div>
  );
}

function Option({ product, handleTempButtonClick, handleSizeButtonClick, selectedTemp, selectedSize }: OptionProps & { selectedTemp: string, selectedSize: string }) {
  return (
    <div className='optionWrap'>
      <div className='sizeButtons'>
        {product.productHasLarge &&
          <button className={selectedSize === "large" ? 'selected' : ''} onClick={() => handleSizeButtonClick("large")}>LARGE</button>
        }
        {product.productHasSmall &&
          <button className={selectedSize === "small" ? 'selected' : ''} onClick={() => handleSizeButtonClick("small")}>SMALL</button>
        }
        {product.productHasMedium &&
          <button className={selectedSize === "medium" ? 'selected' : ''} onClick={() => handleSizeButtonClick("medium")}>MEDIUM</button>
        }
      </div>
      <div className='tempButtons'>
        {product.productHasHot &&
          <button className={selectedTemp === "hot" ? 'selected' : ''} onClick={() => handleTempButtonClick("hot")}>HOT</button>
        }
        {product.productHasIce &&
          <button className={selectedTemp === "ice" ? 'selected' : ''} onClick={() => handleTempButtonClick("ice")}>ICE</button>
        }
      </div>
    </div>
  );
}

export default MenuOption;
