export default function CategoryNavbar({categories, handleCategoryClick, activeCategoryId}: CategoryNavbarProps) {
  const { transcript, listening, toggleListening } = useSpeechToText();

  useEffect(() => {
    // categories 배열이 비어있지 않은 경우에만 첫 번째 카테고리의 상품을 표시
    if (categories.length > 0) {
      handleCategoryClick(categories[0].categoryId);
    }
  }, [categories]); 

  useEffect(() => {
    // transcript 값이 변경될 때마다 실행되어 일치하는 카테고리 버튼을 누름
    const matchingCategory = categories.find(category => category.categoryName === transcript);
    if (matchingCategory) {
      handleCategoryClick(matchingCategory.categoryId);
    }
  }, [transcript, categories, handleCategoryClick]);

  return (
   <>
   <div className={styles.CategoryWrapper}>
        <div className={styles.CategoryTitle}>개발자 키오스크</div>
        <textarea className="transcript" value={transcript} onChange={() => {}} />
        <button onClick={toggleListening}>
          {listening ? '음성인식 중지' : '음성인식 시작'}
        </button>
   </div>
   <div className={styles.categoryNavbar}>
     {categories.map(category => (
       <CategoryButton
         categoryId={category.categoryId}
         categoryName={category.categoryName}
         key={category.categoryId} 
         isActive={category.categoryId === activeCategoryId}
         onclick={() => handleCategoryClick(category.categoryId)} 
       />
     ))}
   </div>
   </>
  );
}
