import React, { useState } from 'react';
import useCartStore from '../store/cart.store';

function Cart() {
  const { cartList } = useCartStore();

  // 각 상품의 수량을 관리하는 상태
  const [productQuantities, setProductQuantities] = useState(
    cartList.reduce((quantities, item) => {
      quantities[item.productId] = item.productAmount;
      return quantities;
    }, {})
  );

  // 수량을 증가시키는 함수
  const increaseQuantity = (productId) => {
    setProductQuantities((prevQuantities) => ({
      ...prevQuantities,
      [productId]: prevQuantities[productId] + 1,
    }));
  };

  // 수량을 감소시키는 함수
  const decreaseQuantity = (productId) => {
    setProductQuantities((prevQuantities) => ({
      ...prevQuantities,
      [productId]: Math.max(prevQuantities[productId] - 1, 0),
    }));
  };

  // 카트에 추가된 각 상품의 정보를 매핑하여 JSX로 변환
  const cartItems = cartList.map((item, index) => (
    <div key={index}>
      <div>상품 정보: {item.productName}, 온도: {item.productTemperature}, 사이즈: {item.productSize}</div>
      <div>수량: 
        <button onClick={() => decreaseQuantity(item.productId)}>-</button>
        {productQuantities[item.productId]}
        <button onClick={() => increaseQuantity(item.productId)}>+</button>
      </div>
    </div>
  ));

  return (
    <div>
      <h2>장바구니</h2>
      <div>
        {cartItems}
        <div>
          총가격: {/* 여기에 총가격을 계산하여 출력하는 코드를 추가하면 됩니다 */}
        </div>
        <div>
          <div>전체 취소</div>
          <div>결제</div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
