  handleAddOrder: (menuOrder:OrderProduct) => void;
  products:
  {
    productName:string;
    productImage:string;
    productPrice:number;
    productHasHot: boolean;
    productHasIce: boolean;
    productHasLarge: boolean;
    productHasMedium: boolean ;
    productHasSmall: boolean ;
    }[];
    setSelectedMenu?: any;
}




export default function MenuItem({products,handleAddOrder}:ProductProps){

  const [isOrderModalOpen, setOrderModal] = useState<boolean>(false);
  const [selectedMenu, setSelectedMenu] = useState<ProductList | undefined>(undefined);
  const closeOrderModal = () => setOrderModal(false);
    return(
        <>
        <div className={styles.menuContainer}>
